<script>
const arriveElement = document.querySelector('#arrive');
const departElement = document.querySelector('#depart');
var fp = flatpickr(document.querySelector('#travelDates'), {
    dateFormat: "m/d/y",
    mode: "range",
    <?php if ( !wp_is_mobile() ) { ?>
    showMonths: 2,
    <?php } ?>
    disableMobile: "true",
    position: "auto right",
    disableMobile: "true",
    minDate: "today",

    onChange: function(selectedDates, dateStr, instance) {
        arriveElement.setAttribute('value', selectedDates[0].toLocaleDateString('en-US', { // you can use undefined as first argument
            year: "2-digit",
            month: "2-digit",
            day: "2-digit",
        }));
        departElement.setAttribute('value', selectedDates[1].toLocaleDateString('en-US', { // you can use undefined as first argument
            year: "2-digit",
            month: "2-digit",
            day: "2-digit",
        }));
    }
});



<?php
if ( !wp_is_mobile() && is_front_page() ) { ?>
$("#booking-mask").removeClass("hidden");
$(".booking-mask--content").addClass("fade-in-down");
$("#booking-mask").addClass("modal-active");


<?php } ?>


$("#book-now").click(function() {
    $("#booking-mask").toggleClass("modal-active");
    $(".booking-mask--content").toggleClass("fade-in-down");
    $("#booking-mask").toggleClass("hidden");
});

$(".modal-close").click(function() {
    closeBookingMask();
});

//close on click
$("#booking-mask").click(function() {
    closeBookingMask();
});

//prevents closing when click inside

$(".booking-mask--content").click(function(event) {
    event.stopPropagation();
});

function closeBookingMask() {
    $("#booking-mask").addClass("hidden");
    $("#book-now").removeClass("modal-active");
}

//tabs on villas
var $btns = $('.cat-btn').click(function() {
    if (this.id == 'all') {
        $('.page-listings > div').fadeIn(450);
    } else {
        var $el = $('.' + this.id).fadeIn(450);
        $('.page-listings > div').not($el).hide();
    }
    $btns.removeClass('active');
    $(this).addClass('active');
})



